$(document).ready(function() {
    // Animate the container on load
    $('.container').addClass('visible');

    // Animate list items and section headers on scroll
    $(window).on('scroll', function() {
        $('.skills li, .certifications li, .additional-skills li, .section-header h3').each(function() {
            var position = $(this).offset().top;
            var topOfWindow = $(window).scrollTop();
            if (position < topOfWindow + $(window).height() - 100) {
                $(this).addClass('visible');
            }
        });
    });

    // Trigger scroll to show items on load
    $(window).trigger('scroll');
});

// Function to download the resume as a PDF
async function downloadPDF() {
    const { jsPDF } = window.jspdf;
    const doc = new jsPDF('p', 'pt', 'a4');

    const content = document.getElementById('resume-content');
    const options = {
        margin: [10, 10, 10, 10],
        filename: 'R_Ratheesh_Resume.pdf',
        image: { type: 'jpeg', quality: 0.98 },
        html2canvas: { scale: 2 },
        jsPDF: { unit: 'pt', format: 'a4', orientation: 'portrait' }
    };

    await doc.html(content, {
        callback: function (doc) {
            doc.save('R_Ratheesh_Resume.pdf');
        },
        x: 10,
        y: 10,
        html2canvas: {
            scale: 0.5,
            width: 600,
            windowWidth: content.scrollWidth
        },
        margin: [10, 10, 10, 10],
    });
}
